DROP TABLE IF EXISTS `measure`;
CREATE TABLE `measure` (
  `measure_id` varchar(32) NOT NULL COMMENT '流水号',
  `measure_no` varchar(32) NOT NULL DEFAULT '' COMMENT '编号',
  `measure_name` varchar(30) NOT NULL DEFAULT '' COMMENT '方案名称',
  `company_id` varchar(32) NOT NULL DEFAULT '' COMMENT '创建人企业id',
  `owner_company_id` varchar(32) NOT NULL DEFAULT '' COMMENT '关联的企业流水号',
  `owner_company_name` varchar(30) NOT NULL DEFAULT '' COMMENT '企业名称',
  `status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态 0 未设置 1 草稿 2 待审核 3 未通过 4 确认中 5 已完成',
  `creator` varchar(32) NOT NULL DEFAULT '' COMMENT '创建人',
  `creator_name` varchar(30) NOT NULL DEFAULT '' COMMENT '创建人名称',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updater` varchar(32) NOT NULL DEFAULT '' COMMENT '最后更新人',
  `updater_name` varchar(32) NOT NULL DEFAULT '' COMMENT '最后更新人名称',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '最后更新（保存）时间',
  `confirmor` varchar(32) NOT NULL DEFAULT '' COMMENT '确认人',
  `confirmor_name` varchar(32) NOT NULL DEFAULT '' COMMENT '确认人名称',
  `confirm_time` datetime DEFAULT NULL COMMENT '确认时间',
  `locker` varchar(32) DEFAULT '' COMMENT '锁定人',
  `locker_name` varchar(32) DEFAULT '' COMMENT '锁定人名称',
  `locker_phone` varchar(32) NOT NULL DEFAULT '' COMMENT '锁定人手机号',
  `is_lock` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否被锁定 0 未锁定 1 已锁定',
  `bind_code` varchar(50) DEFAULT '' COMMENT '微咨询方案code',
  `bind_id` varchar(32) DEFAULT '' COMMENT '微咨询方案id',
  `talent_valuation_id` varchar(32) NOT NULL DEFAULT '' COMMENT '人才评估方案id',
  `job_valuation_id` varchar(32) NOT NULL DEFAULT '' COMMENT '岗位评估方案id',
  `lock_platform` tinyint(4) NOT NULL DEFAULT '0' COMMENT '锁定测算所在的平台 0 未设置 1 顾问平台 2 企业平台',
  `create_platform` tinyint(4) NOT NULL DEFAULT '0' COMMENT '创建测算的平台 0 未设置 1 顾问平台 2 企业平台',
  `author` varchar(32) NOT NULL DEFAULT '' COMMENT '作者',
  `author_name` varchar(32) NOT NULL DEFAULT '' COMMENT '作者名称',
  `company_name` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`measure_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='测算';

